import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [chatStatus, setChatStatus] = useState('ended'); 
  const messagesEndRef = useRef(null);

  const initialChatRequest = {
    "message": "Write a quick manuscript", 
    "agents_info": [
        {
            "name": "Personal_Assistant",
            "type": "AssistantAgent",
            "llm": {
                "model": "gpt-4o"
            },
            "system_message": "You are a personal assistant who can answer questions.",
            "description": "This is a personal assistant who can answer questions."
        }
    ],
    "task_info": {
        "id": 0,
        "name": "Personal Assistant",
        "description": "This is a powerful personal assistant.",
        "maxMessages": 5,
        "speakSelMode": "auto"
    }
  };

  const handleSend = async (text) => {
    const messageToSend = text || userInput;
    const apiEndpoint = chatStatus === 'ended' ? 'http://localhost:5008/api/start_chat' : 'http://localhost:5008/api/send_message';
    const requestBody = chatStatus === 'ended' 
      ? { ...initialChatRequest, message: messageToSend }
      : { message: messageToSend };

    try {
      const response = await fetch(apiEndpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        throw new Error('Failed to send request');
      }

      const data = await response.json(); // Assuming the server sends back some data
      setMessages([...messages, {user: 'User', message: messageToSend}, {user: 'Agent', message: data.reply}]); // Update messages to include both user and agent reply
      setChatStatus('ongoing'); // Update chat status as ongoing
      setUserInput(''); // Clear input field after sending
    } catch (error) {
      console.error('Error sending request:', error);
    }
  };

  useEffect(() => {
    const intervalId = setInterval(fetchMessages, 1000); 
    return () => clearInterval(intervalId);
  }, [messages]); 

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const questions = [
    "Can you describe the symptoms you've been experiencing?",
    "How long have you been feeling unwell?",
    "Can you describe any symptoms you've been experiencing related to your diabetes?",
    "Have you noticed any chest pain, shortness of breath, or palpitations recently?"
  ];

  return (
    <div className="App">
      <div className="chat-window">
        <div className="messages">
          <div className="message system">
            Hello! Think of me as your patient simulation bot, here to help you practice and refine your medical skills. Please go ahead and ask your questions as if I were your patient. Here are a few questions to get you started:
            {questions.map((question, index) => (
              <div key={index} className="question-link" onClick={() => handleSend(question)}>
                - {question}
              </div>
            ))}
          </div>
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.user === 'User_Proxy' ? 'user' : 'agent'}`}>
              <strong>{msg.user}:</strong> {msg.message}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <div className="input-area">
          <input
            type="text"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            placeholder="Type your message..."
          />
          <button onClick={() => handleSend()}>Send</button>
        </div>
        <p className="chat-status">Chat Status: {chatStatus}</p>
      </div>
    </div>
  );
}

export default App;
